
public class Pattern18 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char a='A';
		for(int i=1;i<=3;i++)
		{
		for(int j=1;j<=3;j++)
		{
			if(j<=i)
			{
			   System.out.print(a);
			}
	    }
		a++;
		System.out.println(" ");
		}
		
	}

}
