
public class Pattern19 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char a='A';
		for(int i=1;i<=3;i++)
		{	a++;
		for(int j=1;j<=3;j++)
		{
			if(j<=i)
			{
			   System.out.print(a);
			}	
	    }
	
		System.out.println(" ");
	
		}
	}

}
